mapper.select:
		SELECT ANONY_NO, ANONY_TITLE, ANONY_CONTENT, CREATE_DATE, EMP_NO
		  FROM ANONY
         LIMIT #{begin}, #{recordPerPage};

SELECT ANONY_NO, ANONY_TITLE, ANONY_CONTENT, CREATE_DATE, EMP_NO
  FROM ANONY
 WHERE ANONY_NO = #{AnonyNo}

mapper.update:
UPDATE ANONY
   SET STATE = 0
 WHERE Anony_NO = #{AnonyNo}
