mapper.select:
SELECT COUNT(*)
  FROM COMMENTS
 WHERE ANONY_NO = #{anonyNo}

mapper.update:
UPDATE COMMENTS
   SET GROUP_NO = #{commentNo}
 WHERE COMMENT_NO = #{commentNo}

mapper.select:
		SELECT C.COMMENT_NO, C.ANONY_NO, C.CREATE_DATE, C.CONTENT, C.STATE, C.DEPTH, C.GROUP_NO, C.EMP_NO
		  FROM EMPLOYEES E INNER JOIN COMMENTS C
		    ON E.EMP_NO = C.EMP_NO
		 WHERE ANONY_NO = #{anoyNo}
         LIMIT #{begin}, #{recordPerPage}

mapper.update:
UPDATE COMMENTS
   SET STATE = -1
 WHERE COMMENT_NO = #{commentNo}
