mapper.select:
SELECT BOARD_NO, EMP_NO, BOARD_TITLE, BOARD_CONTENT, CREATE_DATE, MODIFY_DATE, HIT, DEPT_NO
  FROM BOARD
 LIMIT #{begin}, #{recordPerPage}

SELECT BOARD_NO, EMP_NO, BOARD_TITLE, BOARD_CONTENT, CREATE_DATE, MODIFY_DATE, HIT, DEPT_NO
  FROM BOARD
 WHERE BOARD_NO = #{boardNo}

mapper.update:
UPDATE BOARD
   SET BOARD_TITLE = #{boardTitle}
     , BOARD_CONTENT = #{boardContent}
     , MODIFY_DATE = NOW()
 WHERE BOARD_NO = #{boardNo}


UPDATE BOARD
   SET STATE = 0
 WHERE BOARD_NO = #{boardNo}

UPDATE BOARD
   SET HIT = HIT + 1
 WHERE BOARD_NO = #{boardNo}
