mapper.select:
SELECT NOTIC_NO, EMP_NO, BOARD_TITLE, BOARD_CONTENT, CREATE_DATE, MODIFY_DATE, HIT
  FROM NOTIC
 WHERE NOTIC_NO = #{noticNo}

mapper.update:
UPDATE NOTIC
   SET BOARD_TITLE = #{boardTitle}
     , BOARD_CONTENT = #{boardContent}
     , MODIFY_DATE = DATE
 WHERE NOTIC_NO = #{noticNo}


UPDATE NOTIC
   SET STATE = 0
 WHERE NOTIC_NO = #{noticNo}

UPDATE NOTIC
   SET HIT = HIT + 1
 WHERE NOTIC_NO = #{noticNo}
