mapper.select:
SELECT COUNT(*)
  FROM COMMENTS
 WHERE BOARD_NO= #{boardNo}

SELECT CMT_NO, BOARD_NO, EMP_NO, CREATE_DATE,CMT_CONTENT, STATE,DEPTH,GROUP_NO
  FROM COMMENTS
 WHERE BOARD_NO= #{boardNo}
 LIMIT #{begin}, #{recordPerPage}

mapper.update:
UPDATE COMMENTS
   SET STATE = -1
 WHERE CMT_NO = #{cmtNo}
